create table service (
    id serial primary key,
    name varchar NOT NULL,                          -- 服务名称
    upstream_id varchar NOT NULL,                   -- 上游ID
    api_set jsonb NOT NULL,                         -- 配置信息
    creator_id integer,                             -- 创建人
    last_editor_id integer,                         -- 编辑人
    abandoned boolean NOT NULL DEFAULT false,       -- 是否弃用
    document_id integer NOT NULL default 0,         -- 文档 ID
    service_type varchar NOT NULL DEFAULT 'others', -- 服务类型 ('extract', 'sort', 'ext&sort', 'others')
    del_unique_key integer default 0,               -- 约束名称唯一删除
    ctime timestamp without time zone not null default current_timestamp,
    last_update_time timestamp without time zone not null  default current_timestamp
);

ALTER TABLE service ADD CONSTRAINT uniq_ser_name UNIQUE (name, del_unique_key);

create table channel (
    id serial primary key,
    name varchar NOT NULL,                    -- 渠道名称
    creator_id integer NOT NULL,              -- 创建人
    last_editor_id integer NOT NULL,          -- 编辑人
    abandoned boolean NOT NULL DEFAULT false, -- 是否弃用
    del_unique_key integer DEFAULT 0,
    ctime timestamp without time zone not null default current_timestamp,
    last_update_time timestamp without time zone not null  default current_timestamp
);

ALTER TABLE channel ADD CONSTRAINT uniq_chn_name UNIQUE (name, del_unique_key);

create table application (
    id serial primary key,
    name varchar NOT NULL,                    -- 应用名称
    channel_id integer NOT NULL,              -- 所属渠道
    service_id integer NOT NULL,              -- 服务id
    route_id varchar NOT NULL,                -- 路由ID
    status integer NOT NULL,                  -- 应用状态
    creator_id integer,                       -- 创建人
    last_editor_id integer,                   -- 编辑人
    abandoned boolean NOT NULL DEFAULT false, -- 是否弃用
    del_unique_key integer NOT NULL DEFAULT 0,
    ctime timestamp without time zone not null default current_timestamp,
    last_update_time timestamp without time zone not null default current_timestamp
);

ALTER TABLE application ADD CONSTRAINT uniq_app_name UNIQUE (name, del_unique_key);

create table users (
    id serial primary key,
    account varchar NOT NULL,                 -- 用户账号
    password varchar NOT NULL,                -- 用户密码
    alias varchar NOT NULL,                   -- 用户名
    role varchar NOT NULL,                    -- 用户角色 ('admin', 'user', 'view')
    status boolean NOT NULL DEFAULT true,     -- 用户状态
    channels jsonb NOT NULL,                  -- 用户下面的渠道
    description varchar NOT NULL DEFAULT '',  -- 用户备注
    abandoned boolean NOT NULL DEFAULT false, -- 是否弃用
    del_unique_key integer NOT NULL DEFAULT 0,
    ctime timestamp without time zone not null default current_timestamp,
    last_update_time timestamp without time zone not null default current_timestamp,
    last_login_time timestamp without time zone,   -- 账号最后登录时间
    pw_expira_time timestamp without time zone   -- 密码过期时间
);

ALTER table users add constraint uniq_user_account unique (account, del_unique_key);

