create table config (
    id   integer,
    name varchar,
    logo varchar
);

create table document (
    id serial primary key,
    content varchar NOT NULL           -- openapi 文档
);

create table versions (
    id serial primary key,
    service_id integer NOT NULL, -- 服务的ID
    version varchar NOT NULL,    -- 服务的版本
    upstream_map jsonb NOT NULL default '[]' -- 上游信息
);

ALTER TABLE versions ADD CONSTRAINT uniq_ser_version UNIQUE (service_id, version);

create table optlogs (
    id serial primary key,
    operation varchar NOT NULL,           -- 操作行为
    resource varchar,                     -- 操作对象信息
    resource_type varchar,                -- 操作对象
    user_id integer NOT NULL,             -- 用户 ID
    req_body varchar NOT NULL,             -- 请求的 body
    opt_time timestamp without time zone not null default current_timestamp
);


CREATE TABLE apisix_route (
    id SERIAL PRIMARY KEY,
    route_id VARCHAR NOT NULL,       -- 通过雪花算法生成的 ID
    content VARCHAR NOT NULL,        -- 保存原本 api 请求和返回的结构类型
    content_yaml VARCHAR NOT NULL,   -- apisix config 配置的格式
    type smallint NOT NULL,          -- 0 下线，1 启用
    status smallint NOT NULL,        -- 0 为前后端使用，1 为管理平台注册的 OCR 服务
    create_at timestamptz NOT NULL DEFAULT CURRENT_TIMESTAMP, -- 创建时间
    update_at timestamptz NOT NULL DEFAULT CURRENT_TIMESTAMP -- 更新时间
);

CREATE TABLE apisix_upstream (
    id SERIAL PRIMARY KEY,
    stream_id VARCHAR NOT NULL,
    content VARCHAR NOT NULL,
    content_yaml VARCHAR NOT NULL,   -- apisix config 配置的格式
    type smallint NOT NULL, -- 0 为前后端使用，1 为管理平台注册的 OCR 服务
    create_at timestamptz NOT NULL DEFAULT CURRENT_TIMESTAMP, -- 创建时间
    update_at timestamptz NOT NULL DEFAULT CURRENT_TIMESTAMP -- 更新时间
);

CREATE TABLE access_log (
    id SERIAL PRIMARY KEY,
    route_id VARCHAR NOT NULL,
    request_id VARCHAR NOT NULL,
    client_addr VARCHAR NOT NULL,
    iso_time VARCHAR NOT NULL,
    timestamp BIGINT NOT NULL,
    datestamp BIGINT NOT NULL,
    minutestamp BIGINT NOT NULL,
    hourstamp BIGINT NOT NULL,
    weeklystamp BIGINT NOT NULL,
    monthstamp BIGINT NOT NULL,
    yearstamp BIGINT NOT NULL,
    time_cost FLOAT NOT NULL,
    request_length INT NOT NULL DEFAULT 0,
    connection VARCHAR NOT NULL,
    connection_requests VARCHAR NOT NULL DEFAULT '',
    uri VARCHAR NOT NULL,
    ori_request VARCHAR NOT NULL,
    query_string VARCHAR DEFAULT '',
    status INT NOT NULL,
    bytes_sent INT NOT NULL,
    referer VARCHAR DEFAULT '',
    user_agent VARCHAR NOT NULL,
    forwarded_for VARCHAR DEFAULT '',
    host VARCHAR DEFAULT '',
    node VARCHAR DEFAULT '',
    upstream VARCHAR NOT NULL DEFAULT ''
);

CREATE INDEX idx_route_id ON access_log (route_id);
CREATE INDEX idx_timestamp ON access_log (timestamp);
CREATE INDEX idx_minutestamp ON access_log (minutestamp);
CREATE INDEX idx_hourstamp ON access_log (hourstamp);
CREATE INDEX idx_datestamp ON access_log (datestamp);
CREATE INDEX idx_weeklystamp ON access_log (weeklystamp);
CREATE INDEX idx_time_cost ON access_log (time_cost);
CREATE INDEX idx_route_id_timestamp ON access_log (route_id, timestamp);

